mapper.select:

SELECT boardnm
  FROM TB_Bbs_Board
 WHERE boardcd = #{boardcd}

SELECT boardcd, boardnm, UseYN 
  FROM TB_Bbs_Board 
 WHERE boardcd = #{boardcd}

mapper.update:
UPDATE TB_Bbs_Board 
    SET boardnm   = #{updateValue.boardnm}
      , UseYN     = #{updateValue.UseYN}
      , UpdateUID = #{updateValue.UpdateUID}
      , UpdateDT  = #{updateValue.UpdateDT}
    WHERE 1 = 1 

      AND boardcd    = #{searchValue.boardcd}


      AND boardnm  = #{searchValue.boardnm}


      AND UseYN    = #{searchValue.UseYN}

mapper.delete:
DELETE FROM TB_Bbs_Board 
 WHERE 1 = 1 

  AND boardcd  = #{boardcd}


  AND boardnm  = #{boardnm}


  AND UseYN    = #{UseYN}

mapper.insert:
INSERT INTO TB_Bbs_Board( boardcd , boardnm , UseYN  )
VALUES

        #{item.boardcd} , #{item.boardnm} , #{item.UseYN}

mapper.select:
SELECT count(*) FROM TB_BBS_Article 
 WHERE boardcd = #{boardcd}

    AND (title LIKE '%${searchWord}%' OR content LIKE '%${searchWord}%')

SELECT articleno, title, regdate, hit, attachfileNum, commentNum FROM ( SELECT @RNUM := @RNUM + 1 AS r, a.* FROM ( SELECT a.articleno articleno, a.title, a.regdate, a.hit, count(distinct(f.attachfileno)) attachfileNum, count(distinct(c.commentno)) commentNum FROM TB_BBS_Article a LEFT OUTER JOIN TB_BBS_Attachfile f ON a.articleno = f.articleno LEFT OUTER JOIN TB_BBS_Comments c ON a.articleno = c.articleno WHERE a.boardcd = #{boardcd}
AND (title LIKE '%${searchWord}%' OR content LIKE '%${searchWord}%')
GROUP BY a.articleno, title, a.regdate, hit ORDER BY articleno DESC ) a, (SELECT @RNUM:=0) b ) temp WHERE r BETWEEN #{start} AND #{end}

SELECT articleno, title, content, email, hit, regdate
FROM TB_BBS_Article
WHERE articleno = #{articleno}

mapper.update:
UPDATE TB_BBS_Article 
   SET title=#{title},content=#{content} 
 WHERE articleno=#{articleno}

mapper.select:
SELECT articleno, title FROM ( SELECT @RNUM := @RNUM + 1 AS r,a.* FROM (SELECT articleno, title FROM TB_BBS_Article WHERE boardcd = #{boardcd} AND articleno = #{articleno}
AND (title LIKE '%${searchWord}%' OR content LIKE '%${searchWord}%')
ORDER BY articleno ) a , (SELECT @RNUM:=0) b ) c WHERE r = 1

SELECT articleno, title FROM ( SELECT @RNUM := @RNUM + 1 AS r,a.* FROM (SELECT articleno, title FROM TB_BBS_Article WHERE boardcd = #{boardcd} AND articleno = #{articleno}
AND (title LIKE '%${searchWord}%' OR content LIKE '%${searchWord}%')
ORDER BY articleno DESC ) a , (SELECT @RNUM:=0) b ) c WHERE r = 1

SELECT attachfileno, filename, filetype, filesize, articleno
  FROM TB_BBS_Attachfile
 WHERE attachfileno = #{attachfileno}

SELECT commentno,articleno,email,memo,regdate 
FROM TB_BBS_Comments 
WHERE commentno = #{commentno}
